import React, {useCallback, useEffect, useState} from 'react';
import './App.css';
import axios, {AxiosResponse} from "axios";
import {Button, Input, Modal, Select, Table} from "antd";
import {ColumnsType} from "antd/es/table";
import {DeleteOutlined, PlusCircleOutlined} from "@ant-design/icons";
import {NotesDataType, INote} from "./App.types";

function App() {
    const [notes, setNotes] = useState<Array<NotesDataType> | null>(null);
    const [isModalOpen, setIsOpenModal] = useState<boolean>(false);
    const [title, setTitle] = useState<string>('');
    const [description, setDescription] = useState<string>('');
    const [priority, setPriority] = useState<number>(1);

    useEffect(() => {
        axios.get('/api/notes').then((response: AxiosResponse<Array<INote>>) => {
            if (response.data) {
                const enrichedData: Array<NotesDataType> = response.data.map(({id, ...rest}) => ({...rest, key: id}));
                setNotes(enrichedData);
            }
        })
    }, []);

    const handleAddNote = useCallback(() => {
        // addNote({title, description, priority}).then((data) => {
        //     if (data.id) {
        //         setTitle('');
        //         setDescription('');
        //         setPriority(1);
        //         getNotes().then((data) => {
        //             if (data) {
        //                 const enrichedData: Array<NotesDataType> = data.map(({id, ...rest}) => ({...rest, key: id}));
        //                 setNotes(enrichedData);
        //             }
        //         })
        //     }
        //     setIsOpenModal(false);
        // })
    }, []);

    const handleDeleteNote = useCallback((id: number) => {
        Modal.confirm({
            title: 'Are you sure want to delete this note?',
            okText: 'Yes',
            okType: 'danger',
            onOk: () => {
                console.log('deleted');
                // deleteNote(id).then((data) => {
                //     getNotes().then((data) => {
                //         const enrichedData = data.map((note) => ({...note, key: note.id}));
                //         setNotes(enrichedData);
                //     })
                // })
            }
        })
    }, []);

    const columns: ColumnsType<NotesDataType> = [
        {
            title: 'id',
            dataIndex: 'id',
            key: 'id',
            render: (_, note) => {
                return <i>{note.key}</i>
            },
        },
        {
            title: 'Title',
            dataIndex: 'title',
            key: 'title',
        },
        {
            title: 'Description',
            dataIndex: 'description',
            key: 'description',
        },
        {
            title: 'Priority',
            key: 'priority',
            dataIndex: 'priority',
        },
        {
            title: 'Created at',
            key: 'date_created',
            dataIndex: 'date_created',
            render: (_, note) => {
                const date = new Date(note.date_created).toUTCString();
                console.log(typeof date)
                return <i>{date}</i>;
            },
        },
        {
            title: '',
            key: 'delete',
            dataIndex: 'delete',
            render: (_, ...rest) => {
                return <DeleteOutlined onClick={() => handleDeleteNote(rest[0].key)}/>;
            },
        },
    ];
    console.log(notes);
    return (
        <div className="App">
            <header className="App-header">
                <p>Notes App</p>
            </header>
            <div className="App-body">
                <div className='Button-wrapper'>
                    <Button className='NewNote-button' onClick={() => setIsOpenModal(true)}><PlusCircleOutlined/> Add
                        note</Button>
                </div>
                {notes && <Table pagination={false} columns={columns} dataSource={notes}/>}
                <Modal
                    title="Add new note"
                    open={isModalOpen}
                    onOk={handleAddNote}
                    okButtonProps={{
                        disabled: !title || !description
                    }}
                    destroyOnClose={true}
                    onCancel={() => setIsOpenModal(false)}
                >
                    <Input className='Modal-component' placeholder='Enter note title' value={title}
                           onChange={(e) => setTitle(e.target.value)}/>
                    <Input className='Modal-component' placeholder='Enter description' value={description}
                           onChange={(e) => setDescription(e.target.value)}/>
                    <Select
                        defaultValue={priority}
                        className='Modal-component'
                        onChange={(value) => setPriority(+value)}
                        options={[
                            {value: 1, label: 1},
                            {value: 2, label: 2},
                            {value: 3, label: 3},
                            {value: 4, label: 4},
                            {value: 5, label: 5},
                        ]}
                    />
                </Modal>
            </div>
        </div>
    );
}

export default App;
